pub enum DrawCommand {
    ClearScreen,
    ClearToColor {
        color: RGBA,
    },
    SetTarget {
        console: usize,
    },
    Set {
        pos: Point,
        color: ColorPair,
        glyph: FontCharType,
    },
    SetBackground {
        pos: Point,
        bg: RGBA,
    },
    Print {
        pos: Point,
        text: String,
    },
    PrintColor {
        pos: Point,
        text: String,
        color: ColorPair,
    },
    PrintRight {
        pos: Point,
        text: String,
    },
    PrintColorRight {
        pos: Point,
        text: String,
        color: ColorPair,
    },
    PrintCentered {
        y: i32,
        text: String,
    },
    PrintColorCentered {
        y: i32,
        text: String,
        color: ColorPair,
    },
    PrintCenteredAt {
        pos: Point,
        text: String,
    },
    PrintColorCenteredAt {
        pos: Point,
        text: String,
        color: ColorPair,
    },
    Printer {
        pos: Point,
        text: String,
        align: TextAlign,
        background: Option<RGBA>,
    },
    Box {
        pos: Rect,
        color: ColorPair,
    },
    HollowBox {
        pos: Rect,
        color: ColorPair,
    },
    DoubleBox {
        pos: Rect,
        color: ColorPair,
    },
    HollowDoubleBox {
        pos: Rect,
        color: ColorPair,
    },
    FillRegion {
        pos: Rect,
        color: ColorPair,
        glyph: FontCharType,
    },
    BarHorizontal {
        pos: Point,
        width: i32,
        n: i32,
        max: i32,
        color: ColorPair,
    },
    BarVertical {
        pos: Point,
        height: i32,
        n: i32,
        max: i32,
        color: ColorPair,
    },
    SetClipping {
        clip: Option<Rect>,
    },
    SetFgAlpha {
        alpha: f32,
    },
    SetBgAlpha {
        alpha: f32,
    },
    SetAllAlpha {
        fg: f32,
        bg: f32,
    },
    SetFancy {
        position: PointF,
        z_order: i32,
        rotation: Radians,
        color: ColorPair,
        glyph: FontCharType,
        scale: PointF,
    },
}

pub enum CommandType {
    Text {
        block: Vec<FontCharType>,
    },
    Centered {
        block: Vec<FontCharType>,
    },
    NewLine {
    },
    Foreground {
        col: RGBA,
    },
    Background {
        col: RGBA,
    },
    TextWrapper {
        block: String,
    },
    Reset {
    },
}

pub enum BEvent {

    /// The window was resized
    Resized {
        new_size: Point,
        dpi_scale_factor: f32,
    },

    /// The window was moved
    Moved {
        new_position: Point,
    },

    /// The window has requested that it be closed
    CloseRequested,

    /// A character was input
    Character {
        c: char,
    },

    /// The window gained or lost focus
    Focused {
        focused: bool,
    },

    /// The mouse cursor entered the window
    CursorEntered,

    /// The mouse cursor left the window
    CursorLeft,

    /// The mouse cursor moved
    CursorMoved {
        position: Point,
    },

    /// A mouse button was pressed or released
    MouseClick {
        button: usize,
        pressed: bool,
    },

    /// Mouse button is down
    MouseButtonDown {
        button: usize,
    },

    /// Mouse button is up
    MouseButtonUp {
        button: usize,
    },

    /// A key on the keyboard was pressed or released.
    KeyboardInput {
        key: VirtualKeyCode,
        scan_code: u32,
        pressed: bool,
    },

    /// The window's scale factor was changed. You generally don't need to do anything for this, unless you are working with
    /// pixel coordinates.
    ScaleFactorChanged {
        new_size: Point,
        dpi_scale_factor: f32,
    },
}
